mapper.select:
SELECT p.id, p.post_url, p.title, p.category_id, p.category_name, p.tags, p.status, p.img_url, p.publish_date, p.create_time FROM t_post p
AND p.category_id = #{categoryId}
AND p.status = #{status}
AND p.title LIKE CONCAT('%',#{title},'%')
ORDER BY p.create_time DESC

SELECT p.id, p.post_url, p.title, p.sub_content, p.category_id, p.category_name, p.tags, p.img_url, p.create_time FROM t_post p
p.status = #{status}
ORDER BY p.create_time DESC

mapper.insert:
INSERT INTO t_post(title,keyword,sub_content,content,status,category_id,category_name,tags,img_url,year,month,day,post_url,publish_date,create_time,update_time)
SELECT #{title},#{keyword},#{subContent},#{content},#{status},#{categoryId},#{categoryName},#{tags},#{imgUrl},#{year},#{month},#{day},#{postUrl},#{publishDate},#{createTime},#{updateTime}
FROM dual
WHERE NOT EXISTS(
      SELECT id
      FROM t_post
      WHERE title = #{title}
)
